        if not case_sensitive:
            input_text = input_text.lower()
            match_texts = {k: v.lower() for k, v in self.match_texts.items()}
        else:
            match_texts = self.match_texts

        for key, value in match_texts.items():
            if value in input_text:
                return key
        return ""

    def route_message(self) -> Data:
        match_key = self.evaluate_condition(self.input_text.text, self.case_sensitive)
        if match_key:
            route_text = self.match_texts[match_key]
            return Data(data={
                "route": route_text,
                "session_id": self.session_id_input.text
            })
        else:
            self.stop("result")
            return None

    def hello_method(self) -> Message:
        matched_keys = self.evaluate_condition(self.input_text.text, self.case_sensitive)
        if len(matched_keys) == 0:
            return Message(text=self.input_text.text)
        else:
            self.stop("hello")
            return None

