class FindNextBookingComponent(Component):
    display_name = "Find Next Booking"
    description = "Find the next booking for a given client email."
    icon = "search"

    inputs = [
        MessageTextInput(
            name="client_email",
            display_name="Client Email",
            info="Email of the client to find the next booking.",
        ),
        DataInput(
            name="bookings_data",
            display_name="Bookings Data",
            info="Data containing all bookings.",
        ),
    ]

    outputs = [
        Output(display_name="Booking Data", name="booking_data", method="process_data"),
    ]

    def process_data(self) -> Data:
        client_email = self.client_email
        bookings_data = self.bookings_data.data  # Access the actual data

        # Ensure bookings_data is properly formatted
        if isinstance(bookings_data, dict) and "bookings" in bookings_data:
            bookings = bookings_data.get("bookings", [])
            
            # Find the next booking for the given client email
            for booking in bookings:
                for attendee in booking.get("attendees", []):
                    if attendee.get("email") == client_email:
                        return Data(data={"result": booking})
        
        self.status = "Booking not found"
        return Data(data={"result": "Booking not found"})

