class ScheduleCheckComponent(Component):
    display_name = "Schedule Check Component"
    description = "Verifica se um dia cai em um final de semana ou feriado e pergunta o horário conveniente."
    icon = "calendar"

    inputs = [
        MessageTextInput(name="data", display_name="Data (DD/MM/AAAA)", info="Data para verificação.")
    ]

    outputs = [
        Output(display_name="Resposta", name="response", method="check_day")
    ]

    # Lista de feriados fixos no Brasil como um atributo de classe
    feriados_fixos = [
        "01/01",  # Confraternização Universal
        "21/04",  # Tiradentes
        "01/05",  # Dia do Trabalho
        "07/09",  # Independência do Brasil
        "12/10",  # Nossa Senhora Aparecida
        "02/11",  # Finados
        "15/11",  # Proclamação da República
        "25/12"   # Natal
    ]

    def check_day(self) -> Message:
        data_input = self.data
        
        try:
            # Converte a string no formato DD/MM/AAAA para um objeto datetime
            data = datetime.strptime(data_input, "%d/%m/%Y")
        except ValueError:
            return "Formato de data inválido. Por favor, use DD/MM/AAAA."
        
        data_str = data.strftime("%d/%m")
        
        if data.weekday() >= 5:
            return Message(text=f"O dia {data_str} cai em um final de semana. Pode me informar um horário em um dia útil?")
        elif data_str in self.feriados_fixos:
            return Message(text=f"O dia {data_str} é um feriado. Pode me informar um horário em um dia útil?")
        else:
            return Message(text=f"Próximos Passos")

