# Component Criado Por: Fabricando sua Ideia https://www.youtube.com/@fabricandosuaideiatutoriais

class ConditionalRouterComponent(Component):
    display_name = "Conditional Router"
    description = "Routes an input message to a corresponding output based on text comparison."
    icon = "equal"
    name = "ConditionalRouter"

    inputs = [
        MessageInput(
            name="input_text",
            display_name="Input Text",
            info="The primary text input for the operation.",
        ),
        BoolInput(
            name="case_sensitive",
            display_name="Case Sensitive",
            info="If true, the comparison will be case sensitive.",
            value=False,
            advanced=True,
        ),
    ]

    outputs = [
        Output(display_name="Route Result", name="result", method="route_message"),
        Output(display_name="Hello", name="hello", method="hello_method"),
        Output(display_name="Confirmation", name="confirmation", method="confirmation_method"),
    ]

    # Definindo os textos de correspondência embutidos
    match_texts = {
        "accepted": "ACCEPTED",
        "cancelled": "CANCELLED"
    }

    confirmation_texts = [
        "Tudo ok, gostaria de confirmar seu agendamento?",
        "Ok, pode me informar a data e hora de interesse?"
    ]

    def evaluate_condition(self, input_text: str, case_sensitive: bool) -> str:
        match_texts = self.match_texts

        if not case_sensitive:
            input_text = input_text.lower()
            match_texts = {k: v.lower() for k, v in match_texts.items()}

        for key, value in match_texts.items():
            if value in input_text:
                return key
        return ""

    def route_message(self) -> Data:
        if not hasattr(self, 'input_text') or self.input_text is None or self.input_text.text is None:
            self.stop("result")
            return None
        
        match_key = self.evaluate_condition(self.input_text.text, self.case_sensitive)
        if match_key:
            route_text = match_key.capitalize()
            return Data(data={"route": route_text})
        else:
            self.stop("result")
            return None

    def hello_method(self) -> Message:
        if not hasattr(self, 'input_text') or self.input_text is None or self.input_text.text is None:
            self.stop("hello")
            return None
        
        match_key = self.evaluate_condition(self.input_text.text, self.case_sensitive)
        if match_key or any(text in self.input_text.text for text in self.confirmation_texts):
            self.stop("hello")
            return None
        else:
            return Message(text=self.input_text.text)

    def confirmation_method(self) -> Message:
        if not hasattr(self, 'input_text') or self.input_text is None or self.input_text.text is None:
            self.stop("confirmation")
            return None
        
        input_text = self.input_text.text
        confirmation_texts = self.confirmation_texts

        if not self.case_sensitive:
            input_text = input_text.lower()
            confirmation_texts = [text.lower() for text in confirmation_texts]

        if any(text in input_text for text in confirmation_texts):
            return Message(text=self.input_text.text)
        else:
            self.stop("confirmation")
            return None
